DROP TABLE IF EXISTS `cultural_company`;
CREATE TABLE `cultural_company` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` varchar(60) NOT NULL DEFAULT '' COMMENT '用户名',
  `name` varchar(100) NOT NULL DEFAULT '' COMMENT '登录邮箱',
  `location` varchar(100) NOT NULL DEFAULT '' COMMENT '用户个人网站',
  `tel` varchar(255) DEFAULT NULL COMMENT '用户头像，相对于upload/avatar目录',
  `contact` varchar(100) DEFAULT NULL,
  `ctel` varchar(50) DEFAULT NULL COMMENT '生日',
  `email` varchar(100) DEFAULT NULL COMMENT '个性签名',
  `code` varchar(50) NOT NULL COMMENT '最后登录ip',
  `code_pic` varchar(100) NOT NULL COMMENT '最后登录时间',
  `create_time` int(11) DEFAULT NULL COMMENT '注册时间',
  `group` varchar(60) NOT NULL DEFAULT '' COMMENT '激活码',
  `group_pic` varchar(100) DEFAULT NULL COMMENT '用户角色id',
  `authorize` int(10) unsigned NOT NULL DEFAULT '0',
  `tip` varchar(255) DEFAULT NULL,
  `logo` varchar(255) DEFAULT NULL,
  `description` text,
  `xm` text,
  `show` text,
  `ct` text,
  PRIMARY KEY (`id`)
) COLLATE='utf8_general_ci' ENGINE=MyISAM;
INSERT INTO cultural_company ( `id`, `user_id`, `name`, `location`, `tel`, `contact`, `ctel`, `email`, `code`, `code_pic`, `create_time`, `group`, `group_pic`, `authorize`, `tip`, `logo`, `description`, `xm`, `show`, `ct` ) VALUES  ('26','0','安徽新媒体集团','','','','','','','','1427424778','','','1','','http://ahwenhui.com/data/upload/5514bd0d1807a.jpg','　　安徽新媒体集团是经省委批准，由省委宣传部主管、省网宣办业务指导、安徽日报报业集团主办的国有独资大型文化企业。领导班子列入省委管理序列，执行省属文化企业相关管理制度。集团依法自主经营，具有独立法人资格。注册资本为八千万元人民币。<br/>

　　安徽新媒体集团坚持社会主义先进文化的前进方向，坚持正确的舆论导向，坚持党管媒体，牢牢把握网上舆论工作的主导权。坚持把社会效益放在首位，充分体现网络新闻媒体的意识形态属性，努力实现社会效益和经济效益的有机统一。坚持遵循文化发展规律，积极适应社会主义市场经济发展要求，培育合格文化市场主体。坚持改革创新，探索建立主管主办管理体制与出资人管理体制有机衔接的工作机制。坚持以人为本，正确处理改革、发展、稳定的关系，充分调动员工的积极性、主动性、创造性。<br/>

　　安徽新媒体集团积极推进全省新闻媒体优质资源整合，大力促进传统媒体与新兴媒体融合发展，着力打造以新闻网站、手机报、客户端、微博微信为代表的新媒体，主动抢占互联网宣传舆论阵地，不断增强我省主流媒体的传播力公信力影响力。
<br/>
